import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './Estilos/slider-container.css';
import YTSearch from 'youtube-api-search';

const ImageSlider = ({ images }) => {
  const [videoUrls, setVideoUrls] = useState([]);

  useEffect(() => {
    // Realiza una búsqueda en YouTube cuando el prop 'videos' o 'apiKey' cambie
    if (videos && apiKey) {
      YTSearch({ key: AIzaSyBVw_XnGYNF1cBst0Hvkt5AqVwFB8-PuqM, term: videos.join(',') }, videos => {
        const videoUrls = videos.map(video => `https://www.youtube.com/embed/${video.id.videoId}`);
        setVideoUrls(videoUrls);
      });
    }
  }, [videos, apiKey]);

  return (
    <motion.div className='slider-container'>
      <motion.div className='slider' drag='x' dragConstraints={{ right: 0 }}>
        {images.map((image, index) => {
          const isImage = image.endsWith('.jpeg') || image.endsWith('.jpg') || image.endsWith('.gif') || image.endsWith('.png');
          
          return (
            <motion.div className='item' key={index}>
              {isImage ? (
                <img src={image} alt='' />
              ) : (
                <iframe title={`video-${index}`} src={image} alt='' />
              )}
            </motion.div>
          );
        })}
      </motion.div>
    </motion.div>
  );
};

export default ImageSlider;






