import React, { useState, useEffect } from 'react';
import './Estilos/slider-container.css'
import { motion } from 'framer-motion'

const ImageSlider = ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);

    // Función para pasar a la siguiente imagen
    const nextSlide = () => {
        const newIndex = (currentIndex + 1) % images.length;
        setCurrentIndex(newIndex);
    };

    // Efecto secundario para cambiar de imagen automáticamente
    useEffect(() => {
        const interval = setInterval(nextSlide, 3000); // Cambia la imagen cada 3 segundos (3000 milisegundos)

        // Limpia el intervalo cuando el componente se desmonta
        return () => clearInterval(interval);
    }, [currentIndex, images.length]); // Asegúrate de incluir images.length en el arreglo de dependencias

    return (
        <div className='slider-container'>
            <motion.div className='slider' drag='x' dragConstraints={{ right: 0 }}>
                {images.map((image, index) => (
                    <motion.div
                        key={index}
                        className={`item ${index === currentIndex ? 'active' : ''}`}
                        initial={{ opacity: 0 }}
                        animate={{ opacity: index === currentIndex ? 1 : 0 }} // Muestra solo la imagen actual
                        exit={{ opacity: 0 }}
                        transition={{ duration: 0.5 }}
                    >
                        <img src={image} alt="" />
                    </motion.div>
                ))}
            </motion.div>
        </div>
    );
}

export default ImageSlider

